    private void createUserButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_createUserButtonActionPerformed
        String sql = "INSERT INTO users(name,email,user,pass,phone,org,date,location,currency) VALUES(?,?,?,?,?,?,?,?,?)";//table name is 'users'

        try {
            final JPanel panel = new JPanel();

            java.util.Date utilDate = dateChooser.getDate();
            java.sql.Date sqlDate = new java.sql.Date(utilDate.getTime());

            String sql2 = "SELECT user FROM users WHERE user=" + "\'" + newUserTextField.getText() + "\'";
            st = (Statement) conn.createStatement(ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_READ_ONLY);
            ResultSet rs2 = st.executeQuery(sql2);
            if (rs2.next()) {

                JOptionPane.showMessageDialog(panel, "Username has already taken.Please choose a unique username.", "Warning",
                        JOptionPane.WARNING_MESSAGE);
                newUserTextField.setText("");
            } else {
                if (!emailTextField.getText().contains("@") || emailTextField.getText().isEmpty()) {
                    JOptionPane.showMessageDialog(panel, "Email address is not valid.", "Warning",
                            JOptionPane.WARNING_MESSAGE);

                } else {
                    String pass = EncryptPass(newPasswordField.getText());
                    if (pass != null) {
                        pst = conn.prepareStatement(sql);
                        pst.setString(1, nameTextField.getText());
                        pst.setString(2, emailTextField.getText());
                        pst.setString(3, newUserTextField.getText());
                        pst.setString(4, pass);
                        pst.setString(5, phoneTextField.getText());
                        pst.setString(6, orgTextField.getText());
                        pst.setDate(7, sqlDate);
                        pst.setString(8, addressTextField.getText() + "," + (String) locationChooser.getSelectedItem());
                        pst.setString(9, (String) currencyChooser.getSelectedItem());
                        pst.execute();
                        clearTextField();
                        JOptionPane.showMessageDialog(null, "Inserted Successfully");
                    } else {
                        JOptionPane.showMessageDialog(panel, "Can not encrypt password.", "Warning",
                                JOptionPane.WARNING_MESSAGE);
                    }
                }

            }

        } catch (Exception e) {
            final JPanel panel = new JPanel();

            JOptionPane.showMessageDialog(panel, "Wrong Input.\nFill up the entire form.", "Warning",
                    JOptionPane.WARNING_MESSAGE);

        }

    }//GEN-LAST:event_createUserButtonActionPerformed

    private String EncryptPass(String passwordString) {

        try {
            String originalInput = passwordString;
            Base64 base64 = new Base64();
            String encodedString = new String(base64.encode(originalInput.getBytes()));
            return encodedString;

        } catch (Exception e) {
            System.out.println(e);
        }
        return null;
    }

    private String DecryptPass(String passwordString) {

        String strDecryptedText = new String();

        try {

            // Get bytes from string
            byte[] byteArray = Base64.decodeBase64(passwordString.getBytes());

            // Print the decoded array
            System.out.println(Arrays.toString(byteArray));

            String decodedString = new String(byteArray);
            // Print the decoded string 
//            System.out.println(passwordString + " = " + decodedString);

            return decodedString;

        } catch (Exception e) {
            System.out.println(e);

        }
        return null;
    }

