    private void passwordFieldKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_passwordFieldKeyPressed
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            try {
                String user = userTextField.getText();
                String pass = EncryptPass(passwordField.getText());
                passwordField.setText("");
                userTextField.setText(null);
                String sql = "SELECT user FROM users WHERE user=" + "\'" + user + "\'";
                st = (Statement) conn.createStatement(ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_READ_ONLY);
                rs = st.executeQuery(sql);
                if (rs.next()) {
                    sql = "SELECT user FROM users WHERE user=" + "\'" + user + "\'" + " AND " + "pass=" + "\'" + pass + "\'";
                    st = (Statement) conn.createStatement(ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_READ_ONLY);
                    rs = st.executeQuery(sql);
                    if (rs.next()) {
                        String query = "update users set active=true where user='" + user + "\'";
                        pst = conn.prepareStatement(query);
                        pst.execute();
                        MainPage mainPage = new MainPage(user);
                        mainPage.setVisible(true);
                        this.setVisible(false);

                    } else {
                        userErrorLabel.setText("Password not matched");
                        userErrorLabel.setVisible(true);
                    }

                } else {
                    userErrorLabel.setText("User not varified");
                    userErrorLabel.setVisible(true);
                }

            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "We can not find your account.");
            }
        }
    }//GEN-LAST:event_passwordFieldKeyPressed

